import React, { useState, useEffect } from 'react';
import './Recipients.css';
import { getRecipients, getRecipientById, createRecipient, updateRecipient, deleteRecipient } from '../services/recipientService';

const Recipients = () => {
  const [recipients, setRecipients] = useState([]);
  const [recipient, setRecipient] = useState(null);
  const [id, setId] = useState('');
  const [updateData, setUpdateData] = useState({ name: '', location: '' });

  useEffect(() => {
    fetchRecipients();
  }, []);

  const fetchRecipients = async () => {
    try {
      const response = await getRecipients();
      setRecipients(response.data);
    } catch (error) {
      console.error('Error fetching recipients:', error);
    }
  };

  const fetchRecipientById = async (id) => {
    try {
      const response = await getRecipientById(id);
      setRecipient(response.data);
      setUpdateData({ name: response.data.name, location: response.data.location });
    } catch (error) {
      console.error('Error fetching recipient by ID:', error);
    }
  };

  const handleGetById = () => {
    fetchRecipientById(id);
  };

  const handleCreate = async () => {
    const newRecipient = { name: 'New Recipient', location: 'Unknown' };  // Replace with actual data
    try {
      await createRecipient(newRecipient);
      fetchRecipients();
    } catch (error) {
      console.error('Error creating recipient:', error);
    }
  };

  const handleUpdate = async (id) => {
    try {
      await updateRecipient(id, updateData);
      fetchRecipients();
    } catch (error) {
      console.error('Error updating recipient:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteRecipient(id);
      fetchRecipients();
    } catch (error) {
      console.error('Error deleting recipient:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdateData({ ...updateData, [name]: value });
  };

  return (
    <div className="container">
      <h1>Recipients</h1>
      <div className="input-group">
        <input type="text" value={id} onChange={(e) => setId(e.target.value)} placeholder="Enter ID" />
        <button onClick={handleGetById}>Get Recipient By ID</button>
      </div>
      <div className="button-group">
        <button onClick={handleCreate}>Create Recipient</button>
      </div>

      <div className="card-container">
        <h2>All Recipients</h2>
        {recipients.map((recipient) => (
          <div className="card" key={recipient.id}>
            <h3>{recipient.name}</h3>
            <p>{recipient.location}</p>
            <input
              type="text"
              name="name"
              value={updateData.name}
              onChange={handleInputChange}
              placeholder="Update Name"
            />
            <input
              type="text"
              name="location"
              value={updateData.location}
              onChange={handleInputChange}
              placeholder="Update Location"
            />
            <button onClick={() => handleUpdate(recipient.id)}>Update</button>
            <button onClick={() => handleDelete(recipient.id)}>Delete</button>
          </div>
        ))}
      </div>

      {recipient && (
        <div className="details">
          <h2>Recipient Details</h2>
          <p>ID: {recipient.id}</p>
          <p>Name: {recipient.name}</p>
          <p>Location: {recipient.location}</p>
        </div>
      )}
    </div>
  );
};

export default Recipients;
