import {
    FETCH_BLOOD_DONATIONS_REQUEST,
    FETCH_BLOOD_DONATIONS_SUCCESS,
    FETCH_BLOOD_DONATIONS_FAILURE
} from '../actions/bloodDonationActions';

const initialState = {
    loading: false,
    bloodDonations: [],
    error: ''
};

export default function bloodDonationReducer(state = initialState, action) {
    switch (action.type) {
        case FETCH_BLOOD_DONATIONS_REQUEST:
            return { ...state, loading: true };
        case FETCH_BLOOD_DONATIONS_SUCCESS:
            return { loading: false, bloodDonations: action.payload, error: '' };
        case FETCH_BLOOD_DONATIONS_FAILURE:
            return { loading: false, bloodDonations: [], error: action.payload };
        default:
            return state;
    }
}
