import React from 'react';
import { motion } from 'framer-motion';
import './Home.css';
import './AuthButtons.css';
import './Footer.css'

const HomePage = () => {
  return (
    <div className="home-page">

      <header className="hero">
      <div className="auth-container">
      <button className="auth-button signup-button">Sign Up</button>
      <button className="auth-button signin-button">Sign In</button>
    </div>
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Welcome to Blood Donation
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Save lives by donating blood.
        </motion.p>
        <motion.button
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5, delay: 1 }}
          className="blood-drip-button"
        >
          Donate Now
        </motion.button>
      </header>

      <section className="info-section">
        <motion.div
          className="info-box"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <h2>Why Donate Blood?</h2>
          <p>
            Every donation can save up to three lives. Donating blood is a simple and selfless way to make a significant impact.
          </p>
        </motion.div>
        <motion.div
          className="info-box"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <h2>Eligibility</h2>
          <p>
            Find out if you're eligible to donate blood. Healthy individuals aged 17 and above can donate.
          </p>
        </motion.div>
        <motion.div
          className="info-box"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <h2>How to Donate</h2>
          <p>
            Locate the nearest blood donation camp and schedule your donation. It's quick, easy, and rewarding.
          </p>
        </motion.div>

      </section>

    </div>


  );
};

export default HomePage;
