import { FETCH_BLOOD_DONATIONS_REQUEST, FETCH_BLOOD_DONATIONS_SUCCESS, FETCH_BLOOD_DONATIONS_FAILURE } from '../actions/types';

const initialState = {
  loading: false,
  bloodDonations: [],
  error: null,
};

export default function(state = initialState, action) {
  switch(action.type) {
    case FETCH_BLOOD_DONATIONS_REQUEST:
      return { ...state, loading: true };
    case FETCH_BLOOD_DONATIONS_SUCCESS:
      return { ...state, loading: false, bloodDonations: action.payload };
    case FETCH_BLOOD_DONATIONS_FAILURE:
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
}
