// src/components/Recipients.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Recipients = () => {
  const [recipients, setRecipients] = useState([]);
  const [recipient, setRecipient] = useState(null);
  const [id, setId] = useState('');

  useEffect(() => {
    fetchRecipients();
  }, []);

  const fetchRecipients = async () => {
    try {
      const response = await axios.get('/api/recipients');
      setRecipients(response.data);
    } catch (error) {
      console.error('Error fetching recipients:', error);
    }
  };

  const fetchRecipientById = async (id) => {
    try {
      const response = await axios.get(`/api/recipients/${id}`);
      setRecipient(response.data);
    } catch (error) {
      console.error('Error fetching recipient by ID:', error);
    }
  };

  const handleGetById = () => {
    fetchRecipientById(id);
  };

  const handleCreate = async () => {
    const newRecipient = { /* your recipient data */ };
    try {
      await axios.post('/api/recipients', newRecipient);
      fetchRecipients();
    } catch (error) {
      console.error('Error creating recipient:', error);
    }
  };

  const handleUpdate = async () => {
    const updatedRecipient = { /* your updated recipient data */ };
    try {
      await axios.put(`/api/recipients/${id}`, updatedRecipient);
      fetchRecipients();
    } catch (error) {
      console.error('Error updating recipient:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/recipients/${id}`);
      fetchRecipients();
    } catch (error) {
      console.error('Error deleting recipient:', error);
    }
  };

  return (
    <div>
      <h1>Recipients</h1>
      <input type="text" value={id} onChange={(e) => setId(e.target.value)} placeholder="Enter ID" />
      <button onClick={handleGetById}>Get Recipient By ID</button>
      <button onClick={handleCreate}>Create Recipient</button>
      <button onClick={handleUpdate}>Update Recipient</button>
      <button onClick={handleDelete}>Delete Recipient</button>

      <div>
        <h2>All Recipients</h2>
        <ul>
          {recipients.map((recipient) => (
            <li key={recipient.id}>{recipient.name}</li>
          ))}
        </ul>
      </div>

      {recipient && (
        <div>
          <h2>Recipient Details</h2>
          <p>ID: {recipient.id}</p>
          <p>Name: {recipient.name}</p>
          {/* Display other recipient details */}
        </div>
      )}
    </div>
  );
};

export default Recipients;
