// src/actions/bloodDonationActions.js
import axios from 'axios';
import { FETCH_BLOOD_DONATIONS_REQUEST, FETCH_BLOOD_DONATIONS_SUCCESS, FETCH_BLOOD_DONATIONS_FAILURE } from './types';

export const fetchBloodDonations = () => async (dispatch) => {
  dispatch({ type: FETCH_BLOOD_DONATIONS_REQUEST });
  try {
    const response = await axios.get('http://localhost:5000/blooddonations');
    dispatch({ type: FETCH_BLOOD_DONATIONS_SUCCESS, payload: response.data });
  } catch (error) {
    dispatch({ type: FETCH_BLOOD_DONATIONS_FAILURE, payload: error });
  }
};
