// src/actions/bloodDonationActions.js

import axios from 'axios';

export const FETCH_BLOOD_DONATIONS_REQUEST = 'FETCH_BLOOD_DONATIONS_REQUEST';
export const FETCH_BLOOD_DONATIONS_SUCCESS = 'FETCH_BLOOD_DONATIONS_SUCCESS';
export const FETCH_BLOOD_DONATIONS_FAILURE = 'FETCH_BLOOD_DONATIONS_FAILURE';

export const fetchBloodDonations = () => async (dispatch) => {
    dispatch({ type: FETCH_BLOOD_DONATIONS_REQUEST });
    try {
        const response = await axios.get('/api/blooddonations');
        dispatch({
            type: FETCH_BLOOD_DONATIONS_SUCCESS,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: FETCH_BLOOD_DONATIONS_FAILURE,
            payload: error.message
        });
    }
};
